
public class Example1 {
	public static void writeSpaces(int number) {
		int dex;
	for(dex = 0; dex < 5; dex++)	{
		System.out.println(number +  " squared = " + (number * number));
		number++;
	}
	}
		public static void main(String[] args) {
		// TODO Auto-generated method stub
writeSpaces(1);

	}

}
